import { useState, useEffect } from "react";

export const useFormatDate = (begginningDate, finishingDate) => {
  const [month, setMonth] = useState<string>("");

  useEffect(() => {
    const begginningMonth = new Date(begginningDate).toLocaleString("default", {
      month: "short",
    });
    const finishingMonth = new Date(finishingDate)
      .toLocaleString("default", {
        month: "short",
      })
      .split(".")
      .join("");

    const firstDay = new Date(begginningDate).getDate();
    const LastDay = new Date(finishingDate).getDate();

    if (begginningMonth === finishingMonth) {
      setMonth(firstDay + "-" + LastDay + begginningMonth);
    } else {
      setMonth(firstDay + begginningMonth + "-" + LastDay + finishingMonth);
    }
  }, []);

  return month;
};
