import React, { useState } from "react";
import styles from "./CarouselCategory.module.scss";

const Categories = [
  {
    pictures: Isles,
    title: "Iles",
  },
  {
    pictures: NationnalsParks,
    title: "Parcs Nationaux",
  },
  {
    pictures: TinyHouses,
    title: "Tiny Houses",
  },
  {
    pictures: Wow,
    title: "Wow !",
  },
  {
    pictures: Playas,
    title: "Plages",
  },
  {
    pictures: Cabanes,
    title: "Cabanes",
  },
  {
    pictures: Design,
    title: "Design",
  },
  {
    pictures: Camping,
    title: "Camping",
  },
];

const CarouselCategory = () => {
  const [active, setActive] = useState("");
  return (
    <div className={styles.container}>
      <ul className={styles.categoriesList}>
        {Categories.map((item) => {
          return (
            <li
              className={`${
                active === item.title ? styles.categoriesItemActive : ""
              } ${styles.categoriesItem}`}
              onClick={() => setActive(item.title)}
              key={item.title}
            >
              <img src={item.pictures} className={styles.images} />
              <span className={styles.title}>{item.title}</span>
            </li>
          );
        })}
      </ul>
    </div>
  );
};

export default CarouselCategory;
