import React from "react";
import "react-responsive-carousel/lib/styles/carousel.min.css"; // requires a loader
import { Carousel } from "react-responsive-carousel";
import styles from "./CardCarousel.module.scss";

interface CardCarouselProps {
  image: string;
}

const CardCarousel = ({ image }: CardCarouselProps) => {
  return (
    <Carousel
      showThumbs={false}
      showArrows={false}
      showIndicators={false}
      infiniteLoop={true}
    >
      {image.map((item) => {
        return (
          <Card
            image={item.image}
            location={item.location}
            sellerStatus={item.sellerStatus}
            begginningDate={item.begginningDate}
            finshingDate={item.finshingDate}
            priceByNight={item.priceByNight}
            stars={item.stars}
          >
            <CardCarousel image={item.image} />
          </Card>
        );
      })}
      <div className={styles.imagesContainer}>
        <img className={styles.images} src={image} />
      </div>
      <div className={styles.imagesContainer}>
        <img className={styles.images} src="https://picsum.photos/200/300" />
      </div>
    </Carousel>
  );
};

export default CardCarousel;
