export const useFormatDate = (begginningDate, finishingDate) => {
  const begginningMonth = new Date(begginningDate).toLocaleString("default", {
    month: "short",
  });
  const finishingMonth = new Date(finishingDate).toLocaleString("default", {
    month: "short",
  });
  const finishingDay = new Date(finishingDate).getDate();
  const begginningDay = new Date(begginningDate).getDay();

  if (begginningMonth != finishingMonth) {
    const month = begginningMonth + finishingMonth;
  } else {
    const month = begginningMonth;
  }
};
