import React, { useState } from "react";
import styles from "./TravelersMenu.module.scss";
import { travelers } from "../const/travelers";
import { TravelersAdd } from "./TravelersAdd";
import { useCounter } from "./../../../../hooks/useCounter";

export const TravelersMenu = () => {
  const { count, increment, decrement } = useCounter(travelers.length);

  const totalTravelers = (count) => {
    console.log(count);
    let total = count.reduce((a, b) => a + b);
    console.log(total);
    return count.reduce((a, b) => a + b);
  };
  const [totalCount, setTotalCount] = useState(totalTravelers(count));
  console.log(totalCount);

  return (
    <div className={styles.container}>
      <ul className={styles.list}>
        {travelers.map((item, i) => {
          return (
            <TravelersAdd
              key={item.title}
              title={item.title}
              subTitle={item.subTitle}
              count={count[i]}
              increment={() => increment(i)}
              decrement={() => decrement(i)}
            />
          );
        })}
      </ul>
    </div>
  );
};
