import React from "react";
import Rating from "../Rating/Rating";
import styles from "./Card.module.scss";
import PriceByNight from "../PriceByNight/PriceByNight";
import CardDescription from "../CardDescription/CardDescription";
import Favorite from "../Favorite/Favorite";
import { useLocation } from "react-router-dom";

interface DestinationsProps {
  image: string[];
  location: string;
  sellerStatus: string;
  begginningDate: Date;
  finishingDate: Date;
  priceByNight: number;
  stars: number;
  children: React.ReactNode;
}

const Card = (props: DestinationsProps) => {
  const formatDate = (begginningDate, finishingDate) => {
    const month = new Date(begginningDate).toLocaleString("default", {
      month: "long",
    });
    const finishingDay = new Date(finishingDate).getDate();
    const begginningDay = new Date(begginningDate).getDay();
    return begginningDate + " " + month;
  };

  return (
    <li className={styles.card}>
      <Favorite />
      {props.children}
      <div className={styles.content}>
        <div className={styles.contentLeft}>
          <CardDescription
            location={props.location}
            sellerStatus={props.sellerStatus}
            date={formatDate(props.begginningDate, props.finishingDate)}
          />
          <PriceByNight price={props.priceByNight} />
        </div>
        <div className={styles.contentRight}>
          <Rating rating={props.stars} />
        </div>
      </div>
    </li>
  );
};

export default Card;
