import { useState, useEffect } from "react";

export const useFormatDate = (begginningDate, finishingDate) => {
  const [month, setMonth] = useState<string>("a");
  const begginningMonth = new Date(begginningDate).toLocaleString("default", {
    month: "short",
  });
  const finishingMonth = new Date(finishingDate).toLocaleString("default", {
    month: "short",
  });

  useEffect(() => {
    const handleClick = (event) => {
      if (ref.current && !ref.current.contains(event.target)) {
        callback();
      }
    };
    document.addEventListener("click", handleClick);

    return () => {
      document.removeEventListener("click", handleClick);
    };
  }, []);
  return month;
};
